:root {
  /* Modern Color Palette */
  --background: hsl(0, 0%, 100%);
  --foreground: hsl(222, 84%, 4.9%);
  --muted: hsl(210, 40%, 98%);
  --muted-foreground: hsl(215, 13.8%, 34.1%);
  --popover: hsl(0, 0%, 100%);
  --popover-foreground: hsl(222, 84%, 4.9%);
  --card: hsl(0, 0%, 100%);
  --card-foreground: hsl(222, 84%, 4.9%);
  --border: hsl(214, 32%, 91%);
  --input: hsl(214, 32%, 91%);
  --primary: hsl(239, 84%, 67%);
  --primary-foreground: hsl(0, 0%, 98%);
  --secondary: hsl(187, 95%, 43%);
  --secondary-foreground: hsl(0, 0%, 98%);
  --accent: hsl(43, 96%, 56%);
  --accent-foreground: hsl(222, 84%, 4.9%);
  --destructive: hsl(0, 84%, 60%);
  --destructive-foreground: hsl(0, 0%, 98%);
  --ring: hsl(239, 84%, 67%);
  --radius: 0.75rem;

  /* Modern Education Theme */
  --edu-primary: hsl(239, 84%, 67%);
  --edu-primary-light: hsl(243, 75%, 59%);
  --edu-primary-dark: hsl(237, 94%, 81%);
  --edu-secondary: hsl(187, 95%, 43%);
  --edu-accent: hsl(43, 96%, 56%);
  --edu-success: hsl(142, 76%, 36%);
  --edu-warning: hsl(43, 96%, 56%);
  --edu-error: hsl(0, 84%, 60%);

  /* Neutral Palette */
  --edu-gray-50: hsl(210, 40%, 98%);
  --edu-gray-100: hsl(210, 40%, 96%);
  --edu-gray-200: hsl(214, 32%, 91%);
  --edu-gray-300: hsl(213, 27%, 84%);
  --edu-gray-400: hsl(215, 20%, 65%);
  --edu-gray-500: hsl(215, 16%, 47%);
  --edu-gray-600: hsl(215, 19%, 35%);
  --edu-gray-700: hsl(215, 25%, 27%);
  --edu-gray-800: hsl(217, 33%, 17%);
  --edu-gray-900: hsl(222, 84%, 4.9%);

  /* Background System */
  --edu-bg-primary: hsl(0, 0%, 100%);
  --edu-bg-secondary: hsl(210, 40%, 98%);
  --edu-bg-tertiary: hsl(210, 40%, 96%);

  /* Light Theme Accents */
  --edu-light-primary: hsl(239, 84%, 97%);
  --edu-light-secondary: hsl(187, 95%, 96%);
  --edu-light-accent: hsl(43, 96%, 96%);
  --edu-light-success: hsl(142, 76%, 96%);
  --edu-light-warning: hsl(43, 96%, 96%);
