}

@layer base {
  * {
    @apply border-border;
    box-sizing: border-box;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
    font-family: 'Inter', 'Segoe UI', 'Roboto', -apple-system, BlinkMacSystemFont, sans-serif;
    background: var(--edu-bg-secondary);
    color: var(--foreground);
    line-height: 1.6;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
  }
}

/* Modern Animations & Effects */
@layer components {
  .card-modern {
    @apply bg-card border border-border rounded-xl shadow-sm;
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .card-modern:hover {
    @apply shadow-lg;
    transform: translateY(-2px);
  }

  .btn-modern {
    @apply inline-flex items-center justify-center rounded-lg font-medium;
    transition: all 0.2s cubic-bezier(0.4, 0, 0.2, 1);
    position: relative;
    overflow: hidden;
  }

  .btn-modern:hover {
    transform: translateY(-1px);
    @apply shadow-md;
  }

  .btn-modern:active {
    transform: translateY(0);
  }

  .btn-primary {
    @apply btn-modern bg-primary text-primary-foreground;
  }

  .btn-primary:hover {
    background: hsl(from var(--primary) h s calc(l - 5%));
  }

  .btn-secondary {
    @apply btn-modern bg-secondary text-secondary-foreground;
  }

  .focus-ring {
    transition: all 0.2s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .focus-ring:focus {
    outline: none;
    box-shadow: 0 0 0 3px hsl(from var(--primary) h s l / 0.2);
    border-color: var(--primary);
  }

  .gradient-primary {
    background: linear-gradient(135deg, var(--edu-primary) 0%, var(--edu-primary-light) 100%);
  }

  .gradient-secondary {
    background: linear-gradient(135deg, var(--edu-secondary) 0%, hsl(from var(--edu-secondary) h s calc(l - 10%)) 100%);
  }

  .glass-effect {
    background: hsl(from var(--background) h s l / 0.8);
    backdrop-filter: blur(12px);
    border: 1px solid hsl(from var(--border) h s l / 0.5);
  }
}

@layer utilities {
  .text-gradient {
    background: linear-gradient(135deg, var(--edu-primary) 0%, var(--edu-secondary) 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  .animate-fade-in {
    animation: fadeIn 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .animate-slide-up {
    animation: slideUp 0.4s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .animate-scale-in {
    animation: scaleIn 0.2s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .animate-pulse-modern {
    animation: pulseModern 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
  }
}

/* Keyframe Animations */
@keyframes fadeIn {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}

@keyframes slideUp {
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes scaleIn {
  from {
    opacity: 0;
    transform: scale(0.95);
  }
  to {
    opacity: 1;
    transform: scale(1);
  }
}

@keyframes pulseModern {
  0%, 100% {
    opacity: 1;
  }
  50% {
    opacity: 0.7;
  }
}

/* Custom Scrollbar */
::-webkit-scrollbar {
  width: 8px;
  height: 8px;
}

::-webkit-scrollbar-track {
  background: var(--muted);
  border-radius: 4px;
}

::-webkit-scrollbar-thumb {
  background: var(--border);
  border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
  background: var(--muted-foreground);
}

