              <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
                <div>
                  <Label htmlFor="internalMarks">Internal Marks</Label>
                  <Input
                    id="internalMarks"
                    type="number"
                    min="0"
                    value={newSemesterResult.internalMarks}
