import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import {
  Clock, Users, BookOpen, 
  GraduationCap, Save, Download, Copy, ChevronLeft, ChevronRight, Calendar, Settings, Plus, Edit, Trash2
} from "lucide-react";
import { Input } from "@/components/ui/input";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Switch } from "@/components/ui/switch";
import { 
  format, startOfMonth, endOfMonth, eachDayOfInterval, startOfWeek, endOfWeek, 
  addWeeks, subWeeks, addMonths, subMonths, addDays, isToday, startOfYear, endOfYear, addYears, subYears
} from "date-fns";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import type { Subject, Teacher, Timetable } from "@shared/schema";

export default function TimetablePage() {
  const { toast } = useToast();
  const [selectedClass, setSelectedClass] = useState("Grade 6");
  const [selectedSection, setSelectedSection] = useState("A");
  const [timetableData, setTimetableData] = useState<any>({});
  const [viewMode, setViewMode] = useState<"weekly" | "monthly" | "yearly">("weekly");
  const [currentDate, setCurrentDate] = useState(new Date());
  const [selectedYear, setSelectedYear] = useState(new Date().getFullYear());
  const [showPeriodManager, setShowPeriodManager] = useState(false);
  const [editingPeriod, setEditingPeriod] = useState<any>(null);
  const [showSubjectManager, setShowSubjectManager] = useState(false);
  const [showSubjectList, setShowSubjectList] = useState(false);
  const [newSubjectName, setNewSubjectName] = useState("");
  const [newSubjectCode, setNewSubjectCode] = useState("");
  const [newSubjectDescription, setNewSubjectDescription] = useState("");

  const classes = ["Grade 1", "Grade 2", "Grade 3", "Grade 4", "Grade 5", "Grade 6", "Grade 7", "Grade 8", "Grade 9", "Grade 10"];
  const sections = ["A", "B", "C"];
  const daysOfWeek = ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday"];

  const { data: subjects } = useQuery({
    queryKey: ["/api/subjects"],
  });

  const { data: teachers } = useQuery({
    queryKey: ["/api/teachers"],
  });

  const { data: timetableEntries } = useQuery({
    queryKey: ["/api/timetable"],
  });

  const { data: periods } = useQuery({
    queryKey: ["/api/periods"],
  });

  // Use dynamic periods from database, fallback to default if not loaded
  const timeSlots = Array.isArray(periods) && periods.length > 0 
    ? periods.map(period => ({
        period: period.periodNumber,
        label: period.label,
        time: `${period.startTime} - ${period.endTime}`,
        isBreak: period.isBreak
      }))
    : [
        { period: 1, label: "Period 1", time: "8:00 - 9:00 AM", isBreak: false },
        { period: 2, label: "Period 2", time: "9:00 - 10:00 AM", isBreak: false },
        { period: 3, label: "Period 3", time: "10:00 - 11:00 AM", isBreak: false },
        { period: 4, label: "Period 4", time: "11:00 - 12:00 PM", isBreak: false },
        { period: 5, label: "Lunch", time: "12:00 - 1:00 PM", isBreak: true },
        { period: 6, label: "Period 5", time: "1:00 - 2:00 PM", isBreak: false },
        { period: 7, label: "Period 6", time: "2:00 - 3:00 PM", isBreak: false },
        { period: 8, label: "Period 7", time: "3:00 - 4:00 PM", isBreak: false }
      ];

  const saveTimetableMutation = useMutation({
    mutationFn: async (data: { entries: any[] }) => {
      return apiRequest("POST", "/api/timetable/bulk", data);
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Timetable saved successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/timetable"] });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to save timetable",
        variant: "destructive",
      });
    },
  });

  const addPeriodMutation = useMutation({
    mutationFn: async (periodData: any) => {
      return apiRequest("POST", "/api/periods", periodData);
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Period added successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/periods"] });
      setShowPeriodManager(false);
      setEditingPeriod(null);
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to add period",
        variant: "destructive",
      });
    },
  });

  const updatePeriodMutation = useMutation({
    mutationFn: async ({ id, data }: { id: number; data: any }) => {
      return apiRequest("PUT", `/api/periods/${id}`, data);
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Period updated successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/periods"] });
      setShowPeriodManager(false);
      setEditingPeriod(null);
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update period",
        variant: "destructive",
      });
    },
  });

  const deletePeriodMutation = useMutation({
    mutationFn: async (id: number) => {
      return apiRequest("DELETE", `/api/periods/${id}`);
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Period deleted successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/periods"] });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to delete period",
        variant: "destructive",
      });
    },
  });

  const addSubjectMutation = useMutation({
    mutationFn: async (subjectData: any) => {
      return apiRequest("POST", "/api/subjects", subjectData);
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Subject added successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/subjects"] });
      setShowSubjectManager(false);
      setNewSubjectName("");
      setNewSubjectCode("");
      setNewSubjectDescription("");
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to add subject",
        variant: "destructive",
      });
    },
  });

  const updateTimetableSlot = (day: string, period: number, subjectId: number | null, teacherId: number | null) => {
    const key = `${selectedClass}-${selectedSection}`;
    const newData = { ...timetableData };

    if (!newData[key]) {
      newData[key] = {};
    }

    const slotKey = `${day}-${period}`;

    if (subjectId) {
      const subject = Array.isArray(subjects) ? subjects.find((s: Subject) => s.id === subjectId) : null;
      const teacher = Array.isArray(teachers) ? teachers.find((t: Teacher) => t.id === teacherId) : null;

      newData[key][slotKey] = {
        subjectId,
        teacherId,
        subjectName: subject?.name || "",
        teacherName: teacher?.name || ""
      };
    } else {
      delete newData[key][slotKey];
    }

    setTimetableData(newData);
  };

  const getTimetableSlot = (day: string, period: number) => {
    // First check local timetable data (for unsaved changes)
    const key = `${selectedClass}-${selectedSection}`;
    const slotKey = `${day}-${period}`;
    const localSlot = timetableData[key]?.[slotKey];

    if (localSlot) {
      return localSlot;
    }

    // Then check database entries (saved timetables)
    if (Array.isArray(timetableEntries)) {
      const dbEntry = timetableEntries.find((entry: any) => 
        entry.class === selectedClass &&
        entry.section === selectedSection &&
        entry.day === day &&
        entry.period === period
      );

      if (dbEntry) {
        const subject = Array.isArray(subjects) ? subjects.find((s: any) => s.id === dbEntry.subjectId) : null;
        const teacher = Array.isArray(teachers) ? teachers.find((t: any) => t.id === dbEntry.teacherId) : null;

        return {
          subjectId: dbEntry.subjectId,
          teacherId: dbEntry.teacherId,
          subjectName: subject?.name,
          teacherName: teacher?.name
        };
      }
    }

    return null;
  };

  const exportTimetable = () => {
    const data = timetableData[`${selectedClass}-${selectedSection}`] || {};
    const csvContent = "data:text/csv;charset=utf-8," + 
      "Day,Time,Subject,Teacher\n" +
      Object.entries(data).map(([key, slot]: [string, any]) => {
        const [day, period] = key.split('-');
        const timeSlot = timeSlots.find(t => t.period === parseInt(period));
        return `${day},${timeSlot?.time || ''},${slot.subjectName},${slot.teacherName}`;
      }).join("\n");

    const encodedUri = encodeURI(csvContent);
    const link = document.createElement("a");
    link.setAttribute("href", encodedUri);
    link.setAttribute("download", `timetable-${selectedClass}-${selectedSection}.csv`);
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const handleSaveTimetable = () => {
    const key = `${selectedClass}-${selectedSection}`;
    const slots = timetableData[key] || {};

    const timetableEntries = Object.entries(slots).map(([slotKey, slot]: [string, any]) => {
      const [day, period] = slotKey.split('-');
      const timeSlot = timeSlots.find(t => t.period === parseInt(period));

      return {
        class: selectedClass,
        section: selectedSection,
        day: day,
        period: parseInt(period),
        startTime: timeSlot?.time.split(' - ')[0] || "09:00",
        endTime: timeSlot?.time.split(' - ')[1] || "10:00",
        subjectId: slot.subjectId,
        teacherId: slot.teacherId,
        room: null
      };
    }).filter(entry => entry.subjectId && entry.teacherId);

    if (timetableEntries.length === 0) {
      toast({
        title: "No Data",
        description: "Please assign subjects and teachers before saving",
        variant: "destructive",
      });
      return;
    }

    saveTimetableMutation.mutate({ entries: timetableEntries });
  };

  const copyTimetableToSection = (targetSection: string) => {
    const sourceKey = `${selectedClass}-${selectedSection}`;
    const targetKey = `${selectedClass}-${targetSection}`;

    if (timetableData[sourceKey]) {
      const newData = { ...timetableData };
      newData[targetKey] = { ...timetableData[sourceKey] };
      setTimetableData(newData);

      toast({
        title: "Timetable Copied",
        description: `Timetable copied to ${selectedClass} Section ${targetSection}`,
      });
    }
  };

  const navigateDate = (direction: "prev" | "next") => {
    if (viewMode === "weekly") {
      const newDate = direction === "next" ? addWeeks(currentDate, 1) : subWeeks(currentDate, 1);
      setCurrentDate(newDate);
    } else if (viewMode === "monthly") {
      const newDate = direction === "next" ? addMonths(currentDate, 1) : subMonths(currentDate, 1);
      setCurrentDate(newDate);
    } else {
      const newYear = direction === "next" ? selectedYear + 1 : selectedYear - 1;
      setSelectedYear(newYear);
      setCurrentDate(new Date(newYear, 0, 1));
    }
  };

  const getViewTitle = (): string => {
    if (viewMode === "weekly") {
      const weekStart = startOfWeek(currentDate, { weekStartsOn: 1 });
      const weekEnd = endOfWeek(currentDate, { weekStartsOn: 1 });
      return `${format(weekStart, "MMM d")} - ${format(weekEnd, "MMM d, yyyy")}`;
    } else if (viewMode === "monthly") {
      return format(currentDate, "MMMM yyyy");
    } else {
      return `Academic Year ${selectedYear}`;
    }
  };

  const getWeekDays = (date: Date): Date[] => {
    const start = startOfWeek(date, { weekStartsOn: 1 });
    return Array.from({ length: 7 }, (_, i) => addDays(start, i)).slice(0, 5); // Monday to Friday
  };

  const getMonthWeeks = (): Date[][] => {
    const monthStart = startOfMonth(currentDate);
    const monthEnd = endOfMonth(currentDate);
    const weeks: Date[][] = [];

    let currentWeekStart = startOfWeek(monthStart, { weekStartsOn: 1 });

    while (currentWeekStart <= monthEnd) {
      const week = getWeekDays(currentWeekStart).filter(date => 
        date >= monthStart && date <= monthEnd
      );
      if (week.length > 0) {
        weeks.push(week);
      }
      currentWeekStart = addWeeks(currentWeekStart, 1);
    }

    return weeks;
  };

  const getYearMonths = (): Date[] => {
    return Array.from({ length: 12 }, (_, i) => new Date(selectedYear, i, 1));
  };

  const renderWeeklyView = () => (
    <div className="overflow-x-auto">
      <table className="w-full border-collapse">
        <thead>
          <tr>
            <th className="border p-3 bg-muted font-semibold">Time / Day</th>
            {getWeekDays(currentDate).map(date => (
              <th key={date.toISOString()} className="border p-3 bg-muted font-semibold min-w-48">
                <div>{format(date, 'EEEE')}</div>
                <div className="text-sm font-normal text-muted-foreground">
                  {format(date, 'MMM d')}
                </div>
              </th>
            ))}
          </tr>
        </thead>
        <tbody>
          {timeSlots.map((timeSlot, slotIndex) => (
            <tr key={`timeslot-${timeSlot.period}-${slotIndex}`}>
              <td className="border p-3 font-medium text-sm bg-muted/50 text-center">
                <div>{timeSlot.label}</div>
                <div className="text-xs text-muted-foreground">{timeSlot.time}</div>
              </td>
              {getWeekDays(currentDate).map((date, index) => {
                const dayName = format(date, 'EEEE');
                if (timeSlot.isBreak) {
                  return (
                    <td key={`${dayName}-${timeSlot.period}`} className="border p-3 text-center bg-orange-50 dark:bg-orange-900/20">
                      <div className="text-orange-600 font-medium">{timeSlot.label}</div>
                    </td>
                  );
                }

                const slotData = getTimetableSlot(dayName, timeSlot.period);
                return (
                  <td key={`${dayName}-${timeSlot.period}`} className="border p-2">
                    <div className="space-y-2">
                      <Select
                        value={slotData?.subjectId?.toString() || ""}
                        onValueChange={(value: string) => {
                          const subjectId = (value && value !== "none") ? parseInt(value) : null;
                          const teacherId = slotData?.teacherId || (Array.isArray(subjects) && subjects.find((s: Subject) => s.id === subjectId) ? subjects[0]?.id : null);
                          updateTimetableSlot(dayName, timeSlot.period, subjectId, teacherId);
                        }}
                      >
                        <SelectTrigger className="h-8 text-xs">
                          <SelectValue placeholder="Select Subject" />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="none">No Subject</SelectItem>
                          {Array.isArray(subjects) && subjects.map((subject: Subject) => (
                            <SelectItem key={subject.id} value={subject.id.toString()}>
                              {subject.name}
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>

                      {slotData?.subjectId && (
                        <Select
                          value={slotData?.teacherId?.toString() || ""}
                          onValueChange={(value: string) => {
                            const teacherId = value ? parseInt(value) : null;
                            updateTimetableSlot(dayName, timeSlot.period, slotData.subjectId, teacherId);
                          }}
                        >
                          <SelectTrigger className="h-8 text-xs">
                            <SelectValue placeholder="Select Teacher" />
                          </SelectTrigger>
                          <SelectContent>
                            {Array.isArray(teachers) && teachers.map((teacher: Teacher) => (
                              <SelectItem key={teacher.id} value={teacher.id.toString()}>
                                {teacher.name}
                              </SelectItem>
                            ))}
                          </SelectContent>
                        </Select>
                      )}

                      {slotData?.subjectName && (
                        <div className="text-xs p-2 bg-blue-100 dark:bg-blue-900/30 rounded border-l-4 border-blue-500">
                          <div className="font-medium">{slotData.subjectName}</div>
                          {slotData.teacherName && (
                            <div className="text-xs opacity-90">{slotData.teacherName}</div>
                          )}
                        </div>
                      )}
                    </div>
                  </td>
                );
              })}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );

  const renderMonthlyView = () => (
    <div className="space-y-4">
      {getMonthWeeks().map((week, weekIndex) => (
        <div key={weekIndex} className="border rounded-lg overflow-hidden">
          <div className="bg-muted p-2 text-sm font-medium">
            Week {weekIndex + 1} - {format(week[0], 'MMM d')} to {format(week[week.length - 1], 'MMM d')}
          </div>
          <div className="grid grid-cols-5 gap-1 p-2">
            {week.map(date => (
              <div key={date.toISOString()} className={`border rounded p-2 min-h-[120px] ${isToday(date) ? 'bg-blue-50 dark:bg-blue-900/20' : ''}`}>
                <div className="text-sm font-medium mb-2">
                  {format(date, 'EEE d')}
                </div>
                <div className="space-y-1">
                  {timeSlots.slice(0, 3).map(timeSlot => {
                    const dayName = format(date, 'EEEE');
                    const slotData = getTimetableSlot(dayName, timeSlot.period);

                    if (timeSlot.isBreak) {
                      return (
                        <div key={timeSlot.period} className="text-xs p-1 bg-orange-100 dark:bg-orange-900/30 rounded">
                          {timeSlot.label}
                        </div>
                      );
                    }

                    return (
                      <div key={timeSlot.period} className="text-xs space-y-1">
                        <div className="font-medium text-muted-foreground">{timeSlot.label}</div>
                        <Select
                          value={slotData?.subjectId?.toString() || ""}
                          onValueChange={(value: string) => {
                            const subjectId = (value && value !== "none") ? parseInt(value) : null;
                            const teacherId = slotData?.teacherId || null;
                            updateTimetableSlot(dayName, timeSlot.period, subjectId, teacherId);
                          }}
                        >
                          <SelectTrigger className="h-6 text-xs">
                            <SelectValue placeholder="Select Subject" />
                          </SelectTrigger>
                          <SelectContent>
                            <SelectItem value="none">No Subject</SelectItem>
                            {Array.isArray(subjects) && subjects.map((subject: Subject) => (
                              <SelectItem key={subject.id} value={subject.id.toString()}>
                                {subject.name}
                              </SelectItem>
                            ))}
                          </SelectContent>
                        </Select>
                        {slotData?.subjectId && (
                          <Select
                            value={slotData?.teacherId?.toString() || ""}
                            onValueChange={(value: string) => {
                              const teacherId = value ? parseInt(value) : null;
                              updateTimetableSlot(dayName, timeSlot.period, slotData.subjectId, teacherId);
                            }}
                          >
                            <SelectTrigger className="h-6 text-xs">
                              <SelectValue placeholder="Select Teacher" />
                            </SelectTrigger>
                            <SelectContent>
                              {Array.isArray(teachers) && teachers.map((teacher: Teacher) => (
                                <SelectItem key={teacher.id} value={teacher.id.toString()}>
                                  {teacher.name}
                                </SelectItem>
                              ))}
                            </SelectContent>
                          </Select>
                        )}
                      </div>
                    );
                  })}
                </div>
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );

  const renderYearlyView = () => (
    <div className="space-y-6">
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4">
        {getYearMonths().map(monthDate => (
          <div key={monthDate.toISOString()} className="border rounded-lg overflow-hidden">
            <div className="bg-gradient-to-r from-blue-500 to-purple-600 text-white p-3 text-center">
              <h3 className="font-semibold">{format(monthDate, 'MMMM')}</h3>
              <p className="text-sm opacity-90">{selectedYear}</p>
            </div>
            <div className="p-3 space-y-2">
              <div className="grid grid-cols-7 gap-1 text-xs">
                {['M', 'T', 'W', 'T', 'F', 'S', 'S'].map((day, i) => (
                  <div key={i} className="text-center font-medium text-muted-foreground p-1">
                    {day}
                  </div>
                ))}
              </div>
              <div className="grid grid-cols-7 gap-1">
                {eachDayOfInterval({
                  start: startOfMonth(monthDate),
                  end: endOfMonth(monthDate)
                }).map(date => {
                  const dayName = format(date, 'EEEE');
                  const hasClasses = timeSlots.slice(0, 3).some(timeSlot => 
                    getTimetableSlot(dayName, timeSlot.period)
                  );
                  const isWeekend = date.getDay() === 0 || date.getDay() === 6;

                  let className = "text-xs p-1 text-center rounded";
                  if (isWeekend) className += " bg-gray-100 dark:bg-gray-800 text-muted-foreground";
                  if (hasClasses && !isWeekend) className += " bg-green-100 dark:bg-green-900/30 text-green-700 dark:text-green-300";
                  if (isToday(date)) className += " ring-2 ring-blue-500";

                  return (
                    <div 
                      key={date.toISOString()} 
                      className={`${className} cursor-pointer hover:bg-blue-100 dark:hover:bg-blue-900/20`}
                      onClick={() => {
                        if (!isWeekend) {
                          setCurrentDate(date);
                          setViewMode("weekly");
                        }
                      }}
                      title={`Click to edit ${format(date, 'MMM d, yyyy')} schedule`}
                    >
                      {format(date, 'd')}
                    </div>
                  );
                })}
              </div>
              <div className="pt-2 border-t text-xs">
                <div className="flex justify-between items-center">
                  <span className="text-muted-foreground">Classes:</span>
                  <span className="font-medium">
                    {eachDayOfInterval({
                      start: startOfMonth(monthDate),
                      end: endOfMonth(monthDate)
                    }).filter(date => {
                      const dayName = format(date, 'EEEE');
                      const isWeekend = date.getDay() === 0 || date.getDay() === 6;
                      return !isWeekend && timeSlots.slice(0, 3).some(timeSlot => 
                        getTimetableSlot(dayName, timeSlot.period)
                      );
                    }).length} days
                  </span>
                </div>
                <div className="mt-1 space-y-1">
                  {Array.from(new Set(
                    eachDayOfInterval({
                      start: startOfMonth(monthDate),
                      end: endOfMonth(monthDate)
                    }).flatMap(date => {
                      const dayName = format(date, 'EEEE');
                      return timeSlots.slice(0, 3).map(timeSlot => 
                        getTimetableSlot(dayName, timeSlot.period)?.subjectName
                      ).filter(Boolean);
                    })
                  )).slice(0, 3).map(subject => (
                    <div key={subject} className="text-xs bg-muted/50 rounded px-1 py-0.5">
                      {subject}
                    </div>
                  ))}
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>

      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Calendar className="h-5 w-5" />
            Academic Calendar Overview - {selectedYear}
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
            <div className="text-center p-4 bg-blue-50 dark:bg-blue-900/20 rounded-lg">
              <div className="text-2xl font-bold text-blue-600">
                {getYearMonths().reduce((total, monthDate) => 
                  total + eachDayOfInterval({
                    start: startOfMonth(monthDate),
                    end: endOfMonth(monthDate)
                  }).filter(date => {
                    const isWeekend = date.getDay() === 0 || date.getDay() === 6;
                    return !isWeekend;
                  }).length, 0
                )}
              </div>
              <div className="text-sm text-muted-foreground">Total School Days</div>
            </div>

            <div className="text-center p-4 bg-green-50 dark:bg-green-900/20 rounded-lg">
              <div className="text-2xl font-bold text-green-600">
                {Array.isArray(subjects) ? subjects.length : 0}
              </div>
              <div className="text-sm text-muted-foreground">Active Subjects</div>
            </div>

            <div className="text-center p-4 bg-purple-50 dark:bg-purple-900/20 rounded-lg">
              <div className="text-2xl font-bold text-purple-600">
                {Array.isArray(teachers) ? teachers.length : 0}
              </div>
              <div className="text-sm text-muted-foreground">Teaching Staff</div>
            </div>

            <div className="text-center p-4 bg-orange-50 dark:bg-orange-900/20 rounded-lg">
              <div className="text-2xl font-bold text-orange-600">
                {classes.length * sections.length}
              </div>
              <div className="text-sm text-muted-foreground">Class Sections</div>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );

  return (
    <div className="min-h-screen bg-background">
      <div className="container mx-auto p-6 space-y-6">
        {/* Header */}
        <div className="flex flex-col md:flex-row md:items-center justify-between gap-4">
          <div>
            <h1 className="text-3xl font-bold text-gradient">Timetable Management</h1>
            <p className="text-muted-foreground mt-1">
              Create and manage {viewMode} class schedules with subject and teacher assignments
            </p>
          </div>
          <div className="flex items-center gap-2">
            <Button
              variant="outline"
              onClick={() => setShowSubjectList(true)}
              className="flex items-center gap-2"
            >
              <BookOpen className="w-4 h-4" />
              View Subjects
            </Button>
            <Button
              variant="outline"
              onClick={() => setShowSubjectManager(true)}
              className="flex items-center gap-2"
            >
              <Plus className="w-4 h-4" />
              Add Subject
            </Button>
            <Button
              variant="outline"
              onClick={() => setShowPeriodManager(true)}
              className="flex items-center gap-2"
            >
              <Settings className="w-4 h-4" />
              Manage Periods
            </Button>
            {/* View Mode Toggle */}
            <div className="flex bg-muted rounded-md p-1">
              <Button
                variant={viewMode === "weekly" ? "default" : "ghost"}
                size="sm"
                onClick={() => setViewMode("weekly")}
                className="h-8"
              >
                Weekly
              </Button>
              <Button
                variant={viewMode === "monthly" ? "default" : "ghost"}
                size="sm"
                onClick={() => setViewMode("monthly")}
                className="h-8"
              >
                Monthly
              </Button>
              <Button
                variant={viewMode === "yearly" ? "default" : "ghost"}
                size="sm"
                onClick={() => setViewMode("yearly")}
                className="h-8"
              >
                Yearly
              </Button>
            </div>

            <Button variant="outline" onClick={exportTimetable}>
              <Download className="w-4 h-4 mr-2" />
              Export Timetable
            </Button>
          </div>
        </div>

        {/* Date Navigation */}
        <Card className="animate-slide-up">
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div className="flex items-center gap-4">
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => navigateDate("prev")}
                >
                  <ChevronLeft className="h-4 w-4" />
                </Button>
                <div className="flex items-center gap-2">
                  <Calendar className="h-4 w-4" />
                  <span className="font-medium text-lg">{getViewTitle()}</span>
                </div>
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => navigateDate("next")}
                >
                  <ChevronRight className="h-4 w-4" />
                </Button>
              </div>

              <Button
                variant="outline"
                size="sm"
                onClick={() => setCurrentDate(new Date())}
              >
                Today
              </Button>
            </div>
          </CardContent>
        </Card>

        {/* Class and Section Selection */}
        <Card className="animate-slide-up">
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div className="flex items-center gap-4">
                <div className="flex items-center gap-2">
                  <Label>Class:</Label>
                  <Select value={selectedClass} onValueChange={setSelectedClass}>
                    <SelectTrigger className="w-48">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      {classes.map(cls => (
                        <SelectItem key={cls} value={cls}>{cls}</SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>
                <div className="flex items-center gap-2">
                  <Label>Section:</Label>
                  <Select value={selectedSection} onValueChange={setSelectedSection}>
                    <SelectTrigger className="w-24">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      {sections.map(section => (
                        <SelectItem key={section} value={section}>{section}</SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>
              </div>
              <div className="flex gap-2">
                <Select onValueChange={copyTimetableToSection}>
                  <SelectTrigger className="w-48">
                    <SelectValue placeholder="Copy to Section..." />
                  </SelectTrigger>
                  <SelectContent>
                    {sections.filter(s => s !== selectedSection).map(section => (
                      <SelectItem key={section} value={section}>Section {section}</SelectItem>
                    ))}
                  </SelectContent>
                </Select>
                <Button onClick={handleSaveTimetable} disabled={saveTimetableMutation.isPending}>
                  <Save className="w-4 h-4 mr-2" />
                  {saveTimetableMutation.isPending ? "Saving..." : "Save Timetable"}
                </Button>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Timetable Grid */}
        <Card className="animate-slide-up" style={{ animationDelay: '200ms' }}>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Clock className="h-5 w-5" />
              {viewMode === "weekly" ? "Weekly" : viewMode === "monthly" ? "Monthly" : "Yearly"} Timetable - {selectedClass} Section {selectedSection}
            </CardTitle>
          </CardHeader>
          <CardContent>
            {viewMode === "weekly" && renderWeeklyView()}
            {viewMode === "monthly" && renderMonthlyView()}
            {viewMode === "yearly" && renderYearlyView()}
          </CardContent>
        </Card>

        {/* Quick Stats */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          <Card className="animate-slide-up" style={{ animationDelay: '300ms' }}>
            <CardContent className="p-6">
              <div className="flex items-center">
                <div className="p-2 bg-blue-100 dark:bg-blue-900/30 rounded-lg mr-4">
                  <BookOpen className="h-6 w-6 text-blue-600" />
                </div>
                <div>
                  <p className="text-sm font-medium text-muted-foreground">Total Subjects</p>
                  <p className="text-2xl font-bold">{Array.isArray(subjects) ? subjects.length : 0}</p>
                </div>
              </div>
            </CardContent>
          </Card>

          <Card className="animate-slide-up" style={{ animationDelay: '400ms' }}>
            <CardContent className="p-6">
              <div className="flex items-center">
                <div className="p-2 bg-green-100 dark:bg-green-900/30 rounded-lg mr-4">
                  <Users className="h-6 w-6 text-green-600" />
                </div>
                <div>
                  <p className="text-sm font-medium text-muted-foreground">Total Teachers</p>
                  <p className="text-2xl font-bold">{Array.isArray(teachers) ? teachers.length : 0}</p>
                </div>
              </div>
            </CardContent>
          </Card>

          <Card className="animate-slide-up" style={{ animationDelay: '500ms' }}>
            <CardContent className="p-6">
              <div className="flex items-center">
                <div className="p-2 bg-purple-100 dark:bg-purple-900/30 rounded-lg mr-4">
                  <GraduationCap className="h-6 w-6 text-purple-600" />
                </div>
                <div>
                  <p className="text-sm font-medium text-muted-foreground">Classes</p>
                  <p className="text-2xl font-bold">{classes.length}</p>
                </div>
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Subject List Dialog */}
        <Dialog open={showSubjectList} onOpenChange={setShowSubjectList}>
          <DialogContent className="max-w-4xl max-h-[80vh] overflow-y-auto">
            <DialogHeader>
              <DialogTitle className="flex items-center gap-2">
                <BookOpen className="w-5 h-5" />
                Subject List
              </DialogTitle>
            </DialogHeader>

            <div className="space-y-4">
              <div className="flex items-center justify-between">
                <p className="text-muted-foreground">
                  Manage all subjects in the system
                </p>
                <Button
                  onClick={() => {
                    setShowSubjectList(false);
                    setShowSubjectManager(true);
                  }}
                  className="flex items-center gap-2"
                >
                  <Plus className="w-4 h-4" />
                  Add New Subject
                </Button>
              </div>

              <div className="grid gap-4">
                {Array.isArray(subjects) && subjects.length > 0 ? (
                  subjects.map((subject: any) => (
                    <div key={subject.id} className="border rounded-lg p-4 bg-card">
                      <div className="flex items-start justify-between">
                        <div className="flex-1">
                          <div className="flex items-center gap-3 mb-2">
                            <div className="w-10 h-10 bg-gradient-to-r from-blue-500 to-purple-600 text-white rounded-lg flex items-center justify-center font-bold text-sm">
                              {subject.code}
                            </div>
                            <div>
                              <h3 className="font-semibold text-lg">{subject.name}</h3>
                              <p className="text-sm text-muted-foreground">Code: {subject.code}</p>
                            </div>
                          </div>
                          {subject.description && (
                            <p className="text-sm text-muted-foreground ml-13">
                              {subject.description}
                            </p>
                          )}
                          <div className="mt-3 flex items-center gap-4 text-xs text-muted-foreground">
                            <span>Subject ID: {subject.id}</span>
                            {subject.createdAt && (
                              <span>Added: {new Date(subject.createdAt).toLocaleDateString()}</span>
                            )}
                          </div>
                        </div>
                        <div className="flex gap-2">
                          <Button
                            size="sm"
                            variant="outline"
                            className="text-blue-600 hover:text-blue-700"
                          >
                            <Edit className="w-4 h-4" />
                          </Button>
                          <Button
                            size="sm"
                            variant="outline"
                            className="text-red-600 hover:text-red-700"
                          >
                            <Trash2 className="w-4 h-4" />
                          </Button>
                        </div>
                      </div>
                    </div>
                  ))
                ) : (
                  <div className="text-center py-12 text-muted-foreground">
                    <BookOpen className="w-12 h-12 mx-auto mb-4 opacity-50" />
                    <h3 className="text-lg font-medium mb-2">No Subjects Found</h3>
                    <p className="text-sm">Add your first subject to get started with timetable management.</p>
                    <Button
                      onClick={() => {
                        setShowSubjectList(false);
                        setShowSubjectManager(true);
                      }}
                      className="mt-4"
                    >
                      <Plus className="w-4 h-4 mr-2" />
                      Add Subject
                    </Button>
                  </div>
                )}
              </div>
            </div>
          </DialogContent>
        </Dialog>

        {/* Subject Management Dialog */}
        <Dialog open={showSubjectManager} onOpenChange={setShowSubjectManager}>
          <DialogContent className="max-w-2xl">
            <DialogHeader>
              <DialogTitle className="flex items-center gap-2">
                <Plus className="w-5 h-5" />
                Add New Subject
              </DialogTitle>
            </DialogHeader>

            <div className="space-y-4">
              <div>
                <Label htmlFor="subjectName">Subject Name *</Label>
                <Input
                  id="subjectName"
                  value={newSubjectName}
                  onChange={(e) => setNewSubjectName(e.target.value)}
                  placeholder="e.g., Advanced Mathematics, Environmental Science"
                />
              </div>
              <div>
                <Label htmlFor="subjectCode">Subject Code *</Label>
                <Input
                  id="subjectCode"
                  value={newSubjectCode}
                  onChange={(e) => setNewSubjectCode(e.target.value.toUpperCase())}
                  placeholder="e.g., AMATH, ENVS"
                  maxLength={10}
                />
              </div>
              <div>
                <Label htmlFor="subjectDescription">Description</Label>
                <Input
                  id="subjectDescription"
                  value={newSubjectDescription}
                  onChange={(e) => setNewSubjectDescription(e.target.value)}
                  placeholder="Brief description of the subject"
                />
              </div>
              <div className="flex gap-3 pt-4">
                <Button
                  onClick={() => {
                    if (!newSubjectName || !newSubjectCode) {
                      toast({
                        title: "Missing Information",
                        description: "Please provide both subject name and code",
                        variant: "destructive",
                      });
                      return;
                    }

                    addSubjectMutation.mutate({
                      name: newSubjectName,
                      code: newSubjectCode,
                      description: newSubjectDescription || null
                    });
                  }}
                  disabled={addSubjectMutation.isPending}
                  className="flex items-center gap-2"
                >
                  {addSubjectMutation.isPending ? (
                    <>
                      <div className="w-4 h-4 animate-spin rounded-full border-2 border-background border-t-transparent" />
                      Adding...
                    </>
                  ) : (
                    <>
                      <Plus className="w-4 h-4" />
                      Add Subject
                    </>
                  )}
                </Button>
                <Button
                  variant="outline"
                  onClick={() => {
                    setNewSubjectName("");
                    setNewSubjectCode("");
                    setNewSubjectDescription("");
                    setShowSubjectManager(false);
                  }}
                >
                  Cancel
                </Button>
              </div>
            </div>
          </DialogContent>
        </Dialog>

        {/* Period Management Dialog */}
        <Dialog open={showPeriodManager} onOpenChange={setShowPeriodManager}>
          <DialogContent className="max-w-4xl max-h-[80vh] overflow-y-auto">
            <DialogHeader>
              <DialogTitle className="flex items-center gap-2">
                <Settings className="w-5 h-5" />
                Period Management
              </DialogTitle>
            </DialogHeader>

            <PeriodManagerComponent />
          </DialogContent>
        </Dialog>
      </div>
    </div>
  );

  function PeriodManagerComponent() {
    const [label, setLabel] = useState("");
    const [startTime, setStartTime] = useState("");
    const [endTime, setEndTime] = useState("");
    const [isBreak, setIsBreak] = useState(false);
    const [periodNumber, setPeriodNumber] = useState(1);

    const resetForm = () => {
      setLabel("");
      setStartTime("");
      setEndTime("");
      setIsBreak(false);
      setPeriodNumber(Array.isArray(periods) ? periods.length + 1 : 1);
      setEditingPeriod(null);
    };

    const handleSubmit = () => {
      if (!label || !startTime || !endTime) {
        toast({
          title: "Error",
          description: "Please fill in all required fields",
          variant: "destructive",
        });
        return;
      }

      const periodData = {
        label,
        startTime,
        endTime,
        isBreak,
        periodNumber,
      };

      if (editingPeriod) {
        updatePeriodMutation.mutate({ id: editingPeriod.id, data: periodData });
      } else {
        addPeriodMutation.mutate(periodData);
      }
    };

    const handleEdit = (period: any) => {
      setEditingPeriod(period);
      setLabel(period.label);
      setStartTime(period.startTime);
      setEndTime(period.endTime);
      setIsBreak(period.isBreak);
      setPeriodNumber(period.periodNumber);
    };

    const handleDelete = (periodId: number) => {
      if (confirm("Are you sure you want to delete this period?")) {
        deletePeriodMutation.mutate(periodId);
      }
    };

    return (
      <div className="space-y-6">
        {/* Current Periods List */}
        <div>
          <div className="flex items-center justify-between mb-4">
            <h3 className="text-lg font-semibold">Current Periods</h3>
            <Button
              onClick={() => {
                resetForm();
              }}
              size="sm"
              className="flex items-center gap-2"
            >
              <Plus className="w-4 h-4" />
              Add Period
            </Button>
          </div>

          <div className="grid gap-3 max-h-60 overflow-y-auto">
            {Array.isArray(periods) && periods.length > 0 ? (
              periods.map((period: any) => (
                <div key={period.id} className="flex items-center justify-between p-4 border rounded-lg bg-card">
                  <div className="flex-1">
                    <div className="flex items-center gap-3">
                      <div className="w-8 h-8 bg-primary text-primary-foreground rounded-full flex items-center justify-center text-sm font-medium">
                        {period.periodNumber}
                      </div>
                      <div>
                        <div className="font-medium">{period.label}</div>
                        <div className="text-sm text-muted-foreground">
                          {period.startTime} - {period.endTime}
                          {period.isBreak && (
                            <span className="ml-2 inline-flex items-center px-2 py-1 rounded-full text-xs bg-orange-100 text-orange-800 dark:bg-orange-900/30 dark:text-orange-300">
                              Break
                            </span>
                          )}
                        </div>
                      </div>
                    </div>
                  </div>
                  <div className="flex gap-2">
                    <Button
                      size="sm"
                      variant="outline"
                      onClick={() => handleEdit(period)}
                    >
                      <Edit className="w-4 h-4" />
                    </Button>
                    <Button
                      size="sm"
                      variant="outline"
                      onClick={() => handleDelete(period.id)}
                      className="text-destructive hover:text-destructive"
                    >
                      <Trash2 className="w-4 h-4" />
                    </Button>
                  </div>
                </div>
              ))
            ) : (
              <div className="text-center py-8 text-muted-foreground">
                No periods configured. Add your first period below.
              </div>
            )}
          </div>
        </div>

        {/* Add/Edit Form */}
        <div className="border-t pt-6">
          <h3 className="text-lg font-semibold mb-4">
            {editingPeriod ? "Edit Period" : "Add New Period"}
          </h3>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <Label htmlFor="label">Period Label *</Label>
              <Input
                id="label"
                value={label}
                onChange={(e) => setLabel(e.target.value)}
                placeholder="e.g., Period 1, Lunch Break, Assembly"
              />
            </div>
            <div>
              <Label htmlFor="periodNumber">Period Number *</Label>
              <Input
                id="periodNumber"
                type="number"
                value={periodNumber}
                onChange={(e) => setPeriodNumber(parseInt(e.target.value) || 1)}
                min="1"
              />
            </div>
            <div>
              <Label htmlFor="startTime">Start Time *</Label>
              <Input
                id="startTime"
                type="time"
                value={startTime}
                onChange={(e) => setStartTime(e.target.value)}
              />
            </div>
            <div>
              <Label htmlFor="endTime">End Time *</Label>
              <Input
                id="endTime"
                type="time"
                value={endTime}
                onChange={(e) => setEndTime(e.target.value)}
              />
            </div>
          </div>

          <div className="flex items-center space-x-2 mt-4">
            <Switch
              id="isBreak"
              checked={isBreak}
              onCheckedChange={setIsBreak}
            />
            <Label htmlFor="isBreak">This is a break period (no subject assignment)</Label>
          </div>

          <div className="flex gap-3 mt-6">
            <Button
              onClick={handleSubmit}
              disabled={addPeriodMutation.isPending || updatePeriodMutation.isPending}
              className="flex items-center gap-2"
            >
              {addPeriodMutation.isPending || updatePeriodMutation.isPending ? (
                <>
                  <div className="w-4 h-4 animate-spin rounded-full border-2 border-background border-t-transparent" />
                  {editingPeriod ? "Updating..." : "Adding..."}
                </>
              ) : (
                <>
                  {editingPeriod ? "Update Period" : "Add Period"}
                </>
              )}
            </Button>
            <Button
              variant="outline"
              onClick={resetForm}
            >
              {editingPeriod ? "Cancel Edit" : "Clear Form"}
            </Button>
          </div>
        </div>
      </div>
    );
  }
}

