import { storage } from "./storage";
import { hashPassword } from "./auth";
import { ROLES } from "./roles";

// Create test users for different roles
export async function createTestUsers() {
  const testUsers = [
    {
      username: "admin",
      password: "admin123",
      name: "System Administrator",
      email: "admin@school.edu",
      role: ROLES.SUPER_ADMIN
    },
    {
      username: "principal",
      password: "principal123",
      name: "John Principal",
      email: "principal@school.edu",
      role: ROLES.PRINCIPAL
    },
    {
      username: "teacher",
      password: "teacher123",
      name: "Sarah Teacher",
      email: "teacher@school.edu",
      role: ROLES.CLASS_TEACHER
    },
    {
      username: "subject_teacher",
      password: "subject123",
      name: "Math Teacher",
      email: "math@school.edu",
      role: ROLES.SUBJECT_TEACHER
    },
    {
      username: "accountant",
      password: "accountant123",
      name: "Finance Manager",
      email: "finance@school.edu",
      role: ROLES.ACCOUNTANT
    },
    {
      username: "student",
      password: "student123",
      name: "Alice Student",
      email: "alice@school.edu",
      role: ROLES.STUDENT
    },
    {
      username: "parent",
      password: "parent123",
      name: "Bob Parent",
      email: "parent@school.edu",
      role: ROLES.PARENT
    },
    {
      username: "staff",
      password: "staff123",
      name: "Non Teaching Staff",
      email: "staff@school.edu",
      role: ROLES.NON_TEACHING
    }
  ];

  console.log("Creating test users...");

  for (const user of testUsers) {
    try {
      // Check if user already exists
      const existingUser = await storage.getUserByUsername(user.username);
      if (!existingUser) {
        const hashedPassword = await hashPassword(user.password);
        await storage.createUser({
          ...user,
          password: hashedPassword
        });
        console.log(`✓ Created test user: ${user.username} (${user.role})`);
      } else {
        console.log(`→ User already exists: ${user.username}`);
      }
    } catch (error) {
      console.error(`✗ Failed to create user ${user.username}:`, error);
    }
  }

  console.log("\nTest users setup complete!");
  console.log("\n=== TEST CREDENTIALS ===");
  console.log("Super Admin: admin / admin123");
  console.log("Principal: principal / principal123");
  console.log("Class Teacher: teacher / teacher123");
  console.log("Subject Teacher: subject_teacher / subject123");
  console.log("Accountant: accountant / accountant123");
  console.log("Student: student / student123");
  console.log("Parent: parent / parent123");
  console.log("Staff: staff / staff123");
  console.log("========================\n");
}

client/src/components/layout/mobile-draw.content:
import { useState } from "react";
import { Link, useLocation } from "wouter";
import { 
  LayoutDashboard,
  Users,
  UserCheck,
  Calendar,
  Clock,
  BookOpen,
  Trophy,
  FileText,
  CreditCard,
  Banknote,
  UserPlus,
  FileCheck,
  CalendarDays,
  Settings,
  Users2,
  LogOut,
  User,
  Shield,
  Menu,
  X
} from "lucide-react";
import { cn } from "@/lib/utils";
import { useAuth } from "@/contexts/AuthContext";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Drawer, DrawerContent, DrawerTrigger, DrawerClose } from "@/components/ui/drawer";

// Define all possible navigation items with their module mappings
const allNavigationItems = [
  { href: "/", label: "Dashboard", icon: LayoutDashboard, module: "dashboard" },
  { href: "/students", label: "Students", icon: Users, module: "students" },
  { href: "/teachers", label: "Teachers", icon: UserCheck, module: "teachers" },
  { href: "/attendance", label: "Attendance", icon: Calendar, module: "attendance" },
  { href: "/calendar", label: "Calendar", icon: CalendarDays, module: "calendar" },
  { href: "/homework", label: "Homework", icon: BookOpen, module: "homework" },
  { href: "/results", label: "Results", icon: Trophy, module: "results" },
  { href: "/reports", label: "Reports", icon: FileText, module: "reports" },
  { href: "/fees", label: "Fee Management", icon: CreditCard, module: "fees" },
  { href: "/payroll", label: "Payroll", icon: Banknote, module: "payroll" },
  { href: "/admissions", label: "Admissions", icon: UserPlus, module: "admissions" },
  { href: "/documents", label: "Documents", icon: FileCheck, module: "documents" },
  { href: "/settings", label: "Settings", icon: Settings, module: "settings" },
  { href: "/users", label: "User Management", icon: Users2, module: "users" },
];

// Role-based permission system
const ROLE_PERMISSIONS = {
  student: ["dashboard", "calendar", "homework", "results", "reports"],
  parent: ["dashboard", "calendar", "homework", "results", "reports"],
  subject_teacher: ["dashboard", "calendar", "homework", "results", "reports"],
  class_teacher: ["dashboard", "attendance", "calendar", "homework", "results", "reports", "fees"],
  non_teaching_staff: ["dashboard"],
  accountant: ["fees", "payroll", "attendance"],
  principal: ["dashboard", "students", "teachers", "attendance", "calendar", "homework", "results", "reports", "fees", "payroll", "admissions", "documents", "settings"],
  admin: ["dashboard", "students", "teachers", "attendance", "calendar", "homework", "results", "reports", "fees", "payroll", "admissions", "documents", "settings"],
  super_admin: ["dashboard", "students", "teachers", "attendance", "calendar", "homework", "results", "reports", "fees", "payroll", "admissions", "documents", "settings", "users"],
};

const getRoleBadgeColor = (role: string) => {
  const colors = {
    super_admin: "bg-red-100 text-red-800 border-red-200",
    admin: "bg-purple-100 text-purple-800 border-purple-200",
    principal: "bg-blue-100 text-blue-800 border-blue-200",
    accountant: "bg-green-100 text-green-800 border-green-200",
    class_teacher: "bg-orange-100 text-orange-800 border-orange-200",
    subject_teacher: "bg-yellow-100 text-yellow-800 border-yellow-200",
    non_teaching_staff: "bg-gray-100 text-gray-800 border-gray-200",
    parent: "bg-pink-100 text-pink-800 border-pink-200",
    student: "bg-indigo-100 text-indigo-800 border-indigo-200",
  };
  return colors[role as keyof typeof colors] || "bg-gray-100 text-gray-800 border-gray-200";
};

const getRoleLabel = (role: string) => {
  const labels = {
    super_admin: "Super Admin",
    admin: "Admin",
    principal: "Principal",
    accountant: "Accountant",
    class_teacher: "Class Teacher",
    subject_teacher: "Subject Teacher",
    non_teaching_staff: "Staff",
    parent: "Parent",
    student: "Student",
  };
  return labels[role as keyof typeof labels] || role;
};

interface MobileDrawerProps {
  isOpen: boolean;
  onOpenChange: (open: boolean) => void;
}

export default function MobileDrawer({ isOpen, onOpenChange }: MobileDrawerProps) {
  const [location] = useLocation();
  const { user, logout } = useAuth();

  const handleLogout = () => {
    logout();
    onOpenChange(false);
  };

  // Filter navigation items based on user role
  const getVisibleNavigationItems = () => {
    if (!user) return [];
    const userRole = user.role || 'student';
    const allowedModules = ROLE_PERMISSIONS[userRole as keyof typeof ROLE_PERMISSIONS] || [];
    return allNavigationItems.filter(item => allowedModules.includes(item.module));
  };

  const navigationItems = getVisibleNavigationItems();

  return (
    <Drawer open={isOpen} onOpenChange={onOpenChange}>
      <DrawerTrigger asChild>
        <Button 
          variant="ghost" 
          size="sm" 
          className="md:hidden"
          onClick={() => onOpenChange(true)}
        >
          <Menu className="h-5 w-5" />
          <span className="sr-only">Open menu</span>
        </Button>
      </DrawerTrigger>

      <DrawerContent className="h-[90vh]">
        <div className="flex flex-col h-full">
          {/* Header */}
          <div className="flex items-center justify-between p-4 border-b border-border">
            <div className="flex items-center space-x-3">
              <div className="w-10 h-10 gradient-primary rounded-lg flex items-center justify-center shadow-lg">
                <Shield className="text-white text-lg" />
              </div>
              <div>
                <h1 className="text-lg font-bold text-gradient">EduManage</h1>
                <p className="text-xs text-muted-foreground">School Management</p>
              </div>
            </div>
            <DrawerClose asChild>
              <Button variant="ghost" size="sm">
                <X className="h-5 w-5" />
                <span className="sr-only">Close menu</span>
              </Button>
            </DrawerClose>
          </div>

          {/* Navigation */}
          <ScrollArea className="flex-1 px-4">
            <nav className="py-4">
              <div className="space-y-2">
                {navigationItems.map((item, index) => {
                  const Icon = item.icon;
                  const isActive = location === item.href || 
                    (item.href !== "/" && location.startsWith(item.href));

                  return (
                    <DrawerClose key={item.href} asChild>
                      <Link href={item.href}>
                        <div
                          className={cn(
                            "flex items-center px-4 py-3 rounded-lg font-medium transition-all duration-200 cursor-pointer",
                            "hover:bg-muted/60",
                            isActive
                              ? "bg-primary/10 text-primary border border-primary/20"
                              : "text-muted-foreground hover:text-foreground"
                          )}
                          onClick={() => onOpenChange(false)}
                        >
                          <Icon className={cn(
                            "mr-3 h-5 w-5",
                            isActive ? "text-primary" : ""
                          )} />
                          <span>{item.label}</span>
                          {isActive && (
                            <div className="ml-auto w-2 h-2 bg-primary rounded-full" />
                          )}
                        </div>
                      </Link>
                    </DrawerClose>
                  );
                })}
              </div>
            </nav>
          </ScrollArea>

          {/* User Profile and Logout */}
          <div className="p-4 border-t border-border">
            <div className="flex items-center space-x-3 mb-3">
              <div className="w-10 h-10 rounded-full bg-gradient-to-r from-blue-500 to-purple-500 flex items-center justify-center">
                <User className="w-5 h-5 text-white" />
              </div>
              <div className="flex-1 min-w-0">
                <p className="text-sm font-medium truncate">{user?.name}</p>
                <Badge variant="outline" className={`text-xs ${getRoleBadgeColor(user?.role || 'student')}`}>
                  {getRoleLabel(user?.role || 'student')}
                </Badge>
              </div>
            </div>
            <Button 
              onClick={handleLogout}
              variant="outline"
              size="sm"
              className="w-full"
            >
              <LogOut className="w-4 h-4 mr-2" />
              Logout
            </Button>
          </div>
        </div>
      </DrawerContent>
    </Drawer>
  );
}
